import { Component } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { catchError } from "rxjs/operators";
import { ServiceService } from "src/app/services/student.service";

@Component({
  selector: "app-student-page",
  templateUrl: "./student-page.component.html",
  styleUrls: ["./student-page.component.css"],
})
export class YourComponent {
  constructor(private http: HttpClient, private errorHandler: ServiceService) {}
  name!: string;
  course!: string;
  email!: string;
  phone!: string;
  errors: =[];
  saveStudent() {
    var inputData = {
      name: this.name,
      course: this.course,
      email: this.email,
      phone: this.phone,
    };
  makeHttpRequest() {
    this.http
      .get("your-api-endpoint")
      .pipe(
        catchError(this.errorHandler.handleError) // Handle errors using the error handler service
      )
      .subscribe(
        (data) => {
          // Handle successful response
        },
        (error) => {
          // Any specific error handling can be done here
        }
      );
  }
}
}
