// your-component.ts
import { Component } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { catchError } from "rxjs/operators";
import { ErrorHandlerService } from "./error-handler.service"; // Import your error handler service

@Component({
  selector: "app-your-component",
  template: ` <!-- Your component template --> `,
})
export class YourComponent {
  constructor(
    private http: HttpClient,
    private errorHandler: ErrorHandlerService
  ) {}

  makeHttpRequest() {
    this.http
      .get("your-api-endpoint")
      .pipe(
        catchError(this.errorHandler.handleError) // Handle errors using the error handler service
      )
      .subscribe(
        (data) => {
          // Handle successful response
        },
        (error) => {
          // Any specific error handling can be done here
        }
      );
  }
}

